import React from 'react';

const Text = (props) =>{
    const buttonStyle = {
        backgroundColor : 'yellow',
        color: 'black',
        padding : '8px',
        textAlign : 'center',
        cursor : 'pointer',
        border : '1px solid black'
     }
     let forLen = 'Text is enough';
     if(props.textLength <= 5 ){
        forLen = 'Text is short';
        buttonStyle.backgroundColor='red';
    }
    return(
        <div>
            <br/>{forLen}<br/><br/>
        <button style={buttonStyle}>Zic</button>
        </div>
        
    )
}

export default Text;
